// reduce toolbar padding from 20px to 10px. save horiz space at border lines and match OS console
export const ThinStyle = {
  paddingLeft: '10px',
  paddingRight: '10px'
};

// align to Toolbar far right
export const AlignRightStyle = {
  marginRight: '-20px'
};
