import { Button } from '@patternfly/react-core/dist/js/components/Button/Button';
import styles from '@patternfly/react-styles/css/components/Table/table';
export const compoundExpand = (value, {
  rowIndex,
  columnIndex,
  rowData,
  column,
  property
}) => {
  if (!value) {
    return null;
  }

  const {
    title,
    props
  } = value;
  const {
    extraParams: {
      onExpand
    }
  } = column;
  const extraData = {
    rowIndex,
    columnIndex,
    column,
    property
  };
  /**
   * @param {React.MouseEvent} event - Mouse event
   */

  function onToggle(event) {
    // tslint:disable-next-line:no-unused-expression
    onExpand && onExpand(event, rowIndex, columnIndex, props.isOpen, rowData, extraData);
  }

  return {
    className: css(styles.tableCompoundExpansionToggle, props.isOpen && styles.modifiers.expanded),
    children: props.isOpen !== undefined && React.createElement(Button, {
      variant: "link",
      onClick: onToggle,
      "aria-expanded": props.isOpen,
      "aria-controls": props.ariaControls
    }, title)
  };
};
