import * as React from 'react';
// import { Button } from '@patternfly/react-core';
import { message, Button } from 'antd';
import '../../../../styles/index.css';
import { style } from 'typestyle';
// import DefaultSecondaryMasthead from '../../../../components/DefaultSecondaryMasthead/DefaultSecondaryMasthead';
// import { RenderContent } from '../../../../components/Nav/Page';
// import { style } from 'typestyle';

// iframe Url
// var metrics_url = "http://metric.ingress.isa.buaanlsde.cn/d/eXPEaNnZz/experiment-metrics?orgId=1&refresh=10s&kiosk=tv"
var yaml_url = "https://yamleditor.ingress.isa.buaanlsde.cn/"

// const containerPadding = style({ padding: '20px 20px 20px 20px' });

interface isState{
    iFrameHeight: string
}

// const openNotification = () => {
//   notification.open({
//     message: 'Notification Title',
//     description:
//       'This is the content of the notification. This is the content of the notification. This is the content of the notification.',
//     onClick: () => {
//       console.log('Notification Clicked!');
//     },
//   });
// };

const info = () => {
  message.success('配置已保存！');
};



const containerPadding = style({ padding: '20px 20px 20px 20px' });

export default class MetricsPage extends React.Component<any,isState> {
    constructor(props: isState) {
        super(props);
        this.state = {
            iFrameHeight: '0px'
        };
    }

    height = 0

    refresh(){
      this.state = {
        iFrameHeight: '0px'
      };
      setTimeout(()=>{
        this.state = {
          iFrameHeight: this.height + 'px'
        };
      },1000)
    }

    render() {
        console.log(yaml_url)
        return (
          <>
            <div className={containerPadding}>
            <iframe ref="iframe" title="yamlEditorUrl" scrolling='no' frameBorder="0" style={{
                width:'100%', height:this.state.iFrameHeight, overflow:'visible'}}
                onLoad={() => {
                    let h = document.documentElement.clientHeight - 314;
                    this.height = h;
                    this.setState({
                        "iFrameHeight": h + 'px'
                    });
                }}
                src={yaml_url}
            />
            <span style={{ float: 'left', paddingLeft: '5px' }}>
            <span style={{ paddingRight: '5px' }}>
              <Button  type="primary" onClick={info}>
                保存
              </Button>
            </span>
            <span style={{ paddingRight: '5px' }}>
              <Button type="primary" onClick={this.refresh}>
                刷新
              </Button>
            </span>
            </span>
            </div>
          </>
        )
    }
}
