import * as React from 'react';
import DefaultSecondaryMasthead from '../../../../components/DefaultSecondaryMasthead/DefaultSecondaryMasthead';
import { RenderContent } from '../../../../components/Nav/Page';
import { UnControlled as CodeMirror } from 'react-codemirror2';

// const getYAMLJS = require('yamljs');

type YamlConfig = { yaml?: string };

const YamlContent = ({ yaml }: YamlConfig) => <pre className="config-yaml">{yaml}</pre>;
YamlContent.displayName = 'Config';


export default class ConfigContent extends React.Component {
  // changeCode = (CodeMirror, changeObj, value) => {if (!value) return;
  //   // 获取 CodeMirror.doc.getValue()
  //   // 赋值 CodeMirror.doc.setValue(value) // 会触发 onChange 事件，小心进入无线递归。
  //   const onChange  = this.props;
  //   let obj = null;
  //   try { // 校验文件是否为 yaml
  //     obj = getYAMLJS.parse(value);
  //     onChange && onChange(obj, true); // 引用父级通过自定义的onChange()函数接收需要的数据
  //   } catch {
  //     CodeMirror.doc.setValue("")
  //   }
  // };

  render() {
    return (
      <>
        <div style={{ backgroundColor: '#fff' }}>
                <DefaultSecondaryMasthead />
        </div>
        <RenderContent>
        <CodeMirror
        // value="请拖入yaml文件"
        options={{
          lineNumbers: true, // 显示行号
          theme: 'ambiance', // 设置主题
          // readOnly: true, // 只读
          mode: { // 实现代码高亮
            name: 'text/x-yaml',
            // name: "javascript", // 没错，需要先引入 javascript
            // json: true
          },
        }}
        // onChange={this.changeCode}
      />
        </RenderContent>
      </>
    );
  }
};
